                    const toggleCheckboxState = () => {
                        const isChecked = !checkbox.checked;
                        checkbox.checked = isChecked;
                        checkboxStates[subArea.value] = isChecked;

                        if (isChecked) {
                            if (!selectedSubAreas.includes(subArea.value)) {
